import { Axios } from "axios"
import { CART_ADD_ITEM } from "../constants/cartConstants";

const addToCart = (productId, qty) => async (dispatch) => {
    try {
        const { data } = await Axios.get(`/api/products/${productId}`);
        dispatch({
            type: CART_ADD_ITEM,
            payload: {
                name: data.name,
                image: data.image,
                price: data.price,
                countInStock: data.countInStock,
                product: data._id,
                seller: data.seller,
                qty,
            },
        });
    } catch (error) {

    }
}

export { addToCart }
